import React from "react"
import ReactDOM from "react-dom"
import "./styles.css"

function sum(a, b) {
    return a + b
}

function primeiroJSX() {
    return (
        <div>
           André Ribeiro - Introdução ao ReactJS
           <h2>soma: {sum(10)}</h2>
        </div>
    )
}

const App = () => {

    return (
        <div className="App">
            Hello World!<br />
            {primeiroJSX()}
        </div>
    )
}

const rootElement = document.getElementById("root")
ReactDOM.render(<App />, rootElement)
